import React, { useEffect, useState } from "react";
import "./Tempapp.css";

export default function Tempapp() {
    const [city, setcity] = useState(null)
    const [search, setSearch] = useState("Delhi")

    useEffect(()=>{ 
        const callApi=async()=>{
            const resp = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${search} &appid=0f664d7de3dc808088144de31ef75b85`)
            const response =await resp.json();
            // console.log(response);
            setcity(response);
        }
        
        callApi();
    }, [])

  return (
    <>
      <div className="box">
        <div className="inputData">
          <input
            type="search"
            className="inputField"
            onChange={(event) => {
                setSearch(event.target.value)
            }}
          />
        </div>
        <div className="info">
          <h2 className="location">
            <i className="fa-solid fa-street-view"></i>{city}
          </h2>
          <h1 className="temp">5.25 Cel</h1>
          <h3 className="tempmin_max">Min : 5.25 Cel | Max :5.25 Cel</h3>
        </div>
        <div className="wave -one"></div>
        <div className="wave -two"></div>
        <div className="wave -three"></div>
      </div>
    </>
  );
}
